mapper.update:
update  tb_store_file
set 
	sp_file_id = #{sp_file_id}
where 
	store_file_id = #{store_file_id}

mapper.select:
select a.store_name, a.store_abbreviation_name, a.business_licenses_name, (select b.pcd_id from tb_pcd b where b.name=a.province_name limit 1) as province_id, (select b.pcd_id from tb_pcd b where b.name=a.city_name limit 1) as city_id, (select b.pcd_id from tb_pcd b where b.name=a.area_name limit 1) as area_id, a.province_name, a.city_name, a.area_name, a.address, a.management_projects_desc, a.city_sort_one, (select b.sort_name from tb_city_file_sort b where b.city_file_sort_id=a.city_sort_one limit 1) city_sort_one_name, (select b.sort_parent_id from tb_city_file_sort b where b.city_file_sort_id=a.city_sort_one limit 1) city_sort_one_parent, a.city_sort_two, (select b.sort_name from tb_city_file_sort b where b.city_file_sort_id=a.city_sort_two limit 1) city_sort_two_name, (select b.sort_parent_id from tb_city_file_sort b where b.city_file_sort_id=a.city_sort_two limit 1) city_sort_two_parent, a.city_sort_three, (select b.sort_name from tb_city_file_sort b where b.city_file_sort_id=a.city_sort_three limit 1) city_sort_three_name, (select b.sort_parent_id from tb_city_file_sort b where b.city_file_sort_id=a.city_sort_three limit 1) city_sort_three_parent, a.principal, a.phone, a.email, a.store_phone, a.open_status, a.user_number, a.phone_bymemeber, a.business_licenses_image_one, a.business_licenses_image_two, a.license_image_one, a.license_image_two, a.license_image_three, a.license_image_four, a.license_image_fix, a.earnest_status, a.earnest_money, a.em_statestatus, a.service_status, a.system_service_fee, a.sf_statestatus, a.starttime, a.endtime, a.store_file_id, a.store_id, a.sp_file_id, a.clerk_file_id, (select b.clerk_name from tb_clerk_file b where a.clerk_file_id=b.clerk_file_id limit 1 ) as clerk_file_name, a.createdate, a.updatedate, a.operate_id, a.biaozhun_status, a.biaozhun_content, a.city_file_fee_id, ifnull(a.isopen_points,'0') as isopen_points, ifnull(a.transaction_points,'0') as transaction_points, c.check_status, c.password, c.merchant_level, c.registertel_phone, c.withdraw_rate from tb_store_file a left join tb_store c on a.store_id=c.store_id where 1=1
and a.store_file_id = #{store_file_id}
and a.store_id = #{store_id}
limit 1

select a.store_name, a.store_abbreviation_name, a.business_licenses_name, a.province_name, a.city_name, a.area_name, a.address, a.management_projects_desc, a.city_sort_one, (select b.sort_name from tb_city_file_sort b where b.city_file_sort_id=a.city_sort_one) city_sort_one_name, (select b.sort_parent_id from tb_city_file_sort b where b.city_file_sort_id=a.city_sort_one) city_sort_one_parent, a.city_sort_two, (select b.sort_name from tb_city_file_sort b where b.city_file_sort_id=a.city_sort_two) city_sort_two_name, (select b.sort_parent_id from tb_city_file_sort b where b.city_file_sort_id=a.city_sort_two) city_sort_two_parent, a.city_sort_three, (select b.sort_name from tb_city_file_sort b where b.city_file_sort_id=a.city_sort_three) city_sort_three_name, (select b.sort_parent_id from tb_city_file_sort b where b.city_file_sort_id=a.city_sort_three) city_sort_three_parent, a.principal, a.phone, a.email, a.store_phone, a.open_status, a.business_licenses_image_one, a.business_licenses_image_two, a.license_image_one, a.license_image_two, a.license_image_three, a.license_image_four, a.earnest_status, a.earnest_money, a.em_statestatus, a.service_status, a.system_service_fee, a.sf_statestatus, a.starttime, a.endtime, a.store_file_id, a.store_id, a.sp_file_id, a.clerk_file_id, a.createdate, a.biaozhun_status, a.updatedate, b.check_status, b.merchant_level from tb_store_file a left join tb_store b on a.store_id=b.store_id where 1=1
and ( a.phone like CONCAT(CONCAT("%",#{pd.content}),"%") or a.store_id like CONCAT(CONCAT("%",#{pd.content}),"%") or a.store_name like CONCAT(CONCAT("%",#{pd.content}),"%") )
and b.check_status=#{pd.check_status}
and a.province_name=#{pd.province_name}
and a.city_name=#{pd.city_name}
and a.area_name=#{pd.area_name}
and a.sp_file_id=#{pd.sp_file_id}
and a.clerk_file_id=#{pd.clerk_file_id}
and find_in_set(a.city_name,#{pd.login_cityname})
and find_in_set(a.area_name,#{pd.login_areaname})
order by a.createdate desc

select a.store_file_id, a.store_name, a.store_abbreviation_name, a.business_licenses_name, a.province_name, (select pcd_id from tb_pcd b where b.name=a.city_name limit 1) as city_id, (select pcd_id from tb_pcd b where b.name=a.area_name limit 1) as area_id, (select pcd_id from tb_pcd b where b.name=a.province_name limit 1) as province_id, a.city_name, a.area_name, a.operate_id, a.sp_file_id, (select c.team_name from tb_sp_file c where c.sp_file_id = a.sp_file_id limit 1) as team_name from tb_store_file a where 1=1
and a.store_name like CONCAT("%",CONCAT(#{pd.content},"%"))
and a.province_name=#{pd.province_name}
and a.city_name=#{pd.city_name}
and a.area_name=#{pd.area_name}
and find_in_set(a.city_name,#{pd.login_cityname})
and find_in_set(a.area_name,#{pd.login_areaname})
order by a.createdate desc
