choose.when:
 						        and a.pay_status=#{pd.pay_status} 

									and date_format(a.createtime,'%Y-%m-%d') >= #{pd.starttime} 


									and date_format(a.createtime,'%Y-%m-%d') <= #{pd.endtime}

 						        and a.pay_status=#{pd.pay_status} 

									and date_format(a.over_time,'%Y-%m-%d') >= #{pd.starttime} 


									and date_format(a.over_time,'%Y-%m-%d') <= #{pd.endtime}

 							 	and a.pay_status in ('1')

									and date_format(a.over_time,'%Y-%m-%d') >= #{pd.starttime} 


									and date_format(a.over_time,'%Y-%m-%d') <= #{pd.endtime}

 							 	and a.pay_status in ('1','97','98')

									and date_format(a.createtime,'%Y-%m-%d') >= #{pd.starttime} 


									and date_format(a.createtime,'%Y-%m-%d') <= #{pd.endtime}


 							 	and a.pay_status in ('1','99')

									and date_format(a.over_time,'%Y-%m-%d') >= #{pd.starttime} 


									and date_format(a.over_time,'%Y-%m-%d') <= #{pd.endtime}

mapper.select:
select a.waterrecord_id , a.user_id , a.user_type , a.nowuser_money , a.money_type , a.money , a.withdraw_rate , a.reduce_money, a.arrivalmoney , a.application_channel , a.remittance_type , a.remittance_name , a.remittance_number , a.pay_status , date_format(a.createtime ,'%Y-%m-%d %H:%i:%s') as createtime, date_format(a.pass_time ,'%Y-%m-%d %H:%i:%s') as pass_time, a.pass_operate_id , a.exception_remark , a.exception_time , a.exception_operator_id , a.chuli_remark , date_format(a.over_time ,'%Y-%m-%d %H:%i:%s') as over_time, a.over_operator_id , a.dakuan_remark , a.exception_status , a.order_tn , a.payee_number , a.jiaoyi_type , a.province_name , a.city_name , a.area_name , a.alipay_money , a.wx_money , a.bank_money , a.nowypay_money from tb_waterrecord a where a.pay_status='1'
and user_id = #{user_id}
and user_type = #{user_type}
and money_type != '5'
and money_type == '5'
and date_format(a.createtime,'%Y-%m-%d') = #{day}
order by a.over_time desc

select
		 count(*)
from 
		tb_waterrecord a
where 
		a.user_id=#{user_id}
		and a.user_type=#{user_type}
		and a.money_type='5'

		and date_format(a.createtime,'%Y-%m-%d') = #{day}

		select 
				 a.money,
				 date_format(a.over_time,'%Y-%m-%d') as over_time 
		from 
				tb_waterrecord a 
 		where  
				1=1
				and a.pay_status='1'

					and
						(
						   a.money = #{pd.content}	 
						)


					and a.province_name=#{pd.province_name}


					and a.city_name=#{pd.city_name}


					and a.area_name=#{pd.area_name}


					and a.user_type=#{pd.user_type}


						and date_format(a.over_time,'%Y-%m') = #{pd.startmonth}


						and  money_type != '5'


						and  money_type == '5'

		group by date_format(a.over_time,'%Y-%m-%d')

select max(b.money) as money, b.user_id, b.over_time, b.user_type from ( select sum(a.money) as money, date_format(a.over_time,'%Y-%m-%d') over_time, a.user_id, a.user_type from tb_waterrecord a
where a.pay_status='1'
and ( a.money = #{pd.content} )
and a.province_name=#{pd.province_name}
and a.city_name=#{pd.city_name}
and a.area_name=#{pd.area_name}
and a.user_type=#{pd.user_type}
and date_format(a.over_time,'%Y-%m') = #{pd.startmonth}
and money_type != '5'
and money_type == '5'
group by date_format(a.over_time,'%Y-%m-%d'),a.user_id ) b GROUP BY b.over_time
