mapper.select:
select a.member_id, a.show_lookid, a.image_url, a.name, a.phone, a.vip_level, a.charm_number, cast(cast((ifnull(a.now_money,'0')) as decimal(10,2)) as char) now_money, cast(cast((ifnull(a.now_integral,'0')) as decimal(10,2)) as char) now_integral, a.isnewmember, a.istuisong, a.islogin, a.createdate, a.logindate, a.wechat_number, a.wxopen_id, a.sex, ifnull(a.recommended,'0') as recommended, ifnull(a.recommended_type,'0') as recommended_type, a.status, a.province_name, a.city_name, a.area_name, a.money_pay_number, a.wechat_pay_number, a.alipay_pay_number, a.bank_pay_number, a.apple_pay_number, a.integral_pay_number, a.consumption, a.redpackage_number, a.longitude, a.latitude, a.onetime_red, a.twotime_red, a.cz_number, a.onecontactintegral, a.twocontactintegral from tb_member a where
a.wbopen_id = #{wbopen_id} a.qqopen_id = #{qqopen_id} a.alipayopen_id = #{alipayopen_id} a.wxopen_id = #{wxopen_id}
order by a.createdate desc limit 1

select a.member_id, a.show_lookid, a.image_url, a.name, a.phone, a.vip_level, a.charm_number, cast(cast((ifnull(a.now_money,'0')) as decimal(10,2)) as char) now_money, cast(cast((ifnull(a.now_integral,'0')) as decimal(10,2)) as char) now_integral, a.isnewmember, a.istuisong, a.islogin, a.createdate, a.logindate, a.wechat_number, a.wxopen_id, a.sex, ifnull(a.recommended,'0') as recommended, ifnull(a.recommended_type,'0') as recommended_type, a.status, a.province_name, a.city_name, a.area_name, a.money_pay_number, a.wechat_pay_number, a.alipay_pay_number, a.bank_pay_number, a.apple_pay_number, a.integral_pay_number, a.consumption, a.redpackage_number, a.longitude, a.latitude, a.onetime_red, a.twotime_red, a.cz_number, a.onecontactintegral, a.twocontactintegral from tb_member a where
a.alipayunionid = #{alipayunionid} a.qqunionid = #{qqunionid} a.wbunionid = #{wbunionid} a.wxunionid = #{wxunionid}
order by a.createdate desc limit 1

select a.redpackage_id, a.member_id, a.redpackage_money, a.redpackage_content, a.redpackage_content as reduce_content, a.set_type, a.enddate, a.startdate, a.enddate as endtime, a.startdate as starttime, a.isguoqi, a.isshiyong, b.store_name from tb_member_redpackets a left join tb_store b on a.set_id=b.store_id where a.member_id=#{member_id}
and redpackage_id=#{redpackage_id}
and store_redpackets_id=#{store_redpackets_id}
and a.isshiyong='0' and a.isguoqi='0'

mapper.update:
update tb_member_vipcard set
total_money=cast(cast(total_money as DECIMAL(10,2))+cast(#{sale_money} as DECIMAL(10,2)) as char),
shop_number=shop_number+1 where member_id=#{member_id} and store_id=#{store_id}

mapper.select:
select a.image_url, a.sex, a.recommended, a.recommended_type, a.name, a.phone, a.province_name, a.city_name, a.area_name, a.vip_level, a.charm_number, a.now_money, a.now_integral, date_format(a.createdate ,'%Y-%m-%d %H:%i:%s') as createdate, a.show_lookid, a.zhuce_shebei, a.member_id from tb_member a where 1=1
and ( a.name like CONCAT(CONCAT("%",#{pd.content}),"%") or a.phone like CONCAT(CONCAT("%",#{pd.content}),"%") )
and a.province_name=#{pd.province_name}
and a.city_name=#{pd.city_name}
and a.area_name=#{pd.area_name}
order by a.createdate desc
