mapper.update:
update tb_member_redpackets set
isguoqi = #{isguoqi},
isshiyong = #{isshiyong},
iszhuanzeng = #{iszhuanzeng},
redpackage_id=redpackage_id where redpackage_id=#{redpackage_id}
and member_id=#{member_id}

mapper.select:
select a.store_redpackets_id, a.member_id, a.redpackage_id, a.redpackage_money, a.redpackage_type, a.redpackage_content, a.enddate, date_format(a.enddate,'%Y-%m-%d') as enddatestr, a.set_id, a.isshiyong, a.iszhuanzeng, a.isguoqi, b.name, b.image_url from tb_member_redpackets a join tb_member b on a.member_id=b.member_id where 1=1
and a.member_id=#{member_id}
and a.redpackage_id=#{redpackage_id}
and a.iszhuanzeng=#{iszhuanzeng}
limit 1
