mapper.update:
update tb_shopcart
set

	goods_number=#{goods_number},
	createdate=now(),

	shopcart_id=shopcart_id
where 
	1=1

	and shopcart_id=#{shopcart_id}


	and goods_id=#{goods_id}


	and member_id=#{member_id}

mapper.delete:
delete from tb_shopcart  
where 
	1=1

		and  shopcart_id = #{shopcart_id}


				and  member_id = #{member_id} 


				and  goods_id=#{goods_id}


				and  goods_type=#{goods_type}

mapper.select:
select date_format(a.createdate, '%Y-%m-%d %H:%i:%s') as createdate , unix_timestamp(a.createdate)*1000 as starttimestamp from tb_shopcart a where a.member_id = #{member_id}
and goods_type=#{goods_type}
order by a.createdate desc limit 1

select a.shopcart_id, a.store_id, a.member_id, a.goods_type, a.goods_name, a.goods_number, a.sale_money, a.goods_id, s.store_name, s.pictrue_url, ifnull(b.gg_imageurl,'') gg_imageurl, ifnull(b.gg_miaosu,'0') gg_miaosu, ifnull(c.th_address,'') th_address, ifnull(c.goods_jfrate,'0') goods_jfrate, ifnull(c.gf_salemoney,'0') gf_salemoney, ifnull(c.goods_dw,'件') goods_dw, ifnull(c.brand_name,'') brand_name, ifnull(g.goods_type,'0') as zcspgoods_type from tb_shopcart a left join tb_store s on a.store_id=s.store_id left join tb_goods g on a.goods_id=g.goods_id left join tb_youxuangg b on a.goods_id=b.youxuangg_id left join tb_youxuangoods c on b.youxuangoods_id=c.youxuangoods_id where a.member_id = #{member_id}
and a.store_id=#{store_id}
and a.goods_type=#{goods_type}
and b.youxuangoods_id=#{youxuangoods_id}

select cast(sum(a.goods_number*a.sale_money) as as decimal(10,2)) as allmoney from tb_shopcart a
left join tb_goods b on a.goods_id=b.goods_id
left join tb_youxuangg b on a.goods_id=b.youxuangg_id
where a.member_id = #{member_id}
and store_id=b.store_id
and goods_type=#{goods_type}
and b.youxuangoods_id=#{youxuangoods_id}

select a.shopcart_id, a.store_id, a.member_id, a.goods_type, a.goods_name, ifnull(a.goods_number,'0') as goods_number, ifnull(a.sale_money,'0') as sale_money, a.goods_id from tb_shopcart a where a.member_id = #{member_id}
and a.store_id=#{store_id}
and a.goods_type=#{goods_type}

select a.shopcart_id, a.store_id, a.member_id, a.goods_type, a.goods_name, ifnull(a.goods_number,'0') as goods_number, ifnull(a.sale_money,'0') as sale_money, ifnull(c.goods_jfrate,'0') as goods_jfrate, ifnull(c.gf_salemoney,'0') as gf_salemoney, a.goods_id from tb_shopcart a left join tb_youxuangg b on a.goods_id=b.youxuangg_id left join tb_youxuangoods c on b.youxuangoods_id=c.youxuangoods_id where 1=1
and a.shopcart_id = #{shopcart_id}
and a.goods_id=#{goods_id} and a.member_id=#{member_id}
limit 1

		select  count(*)
		from tb_shopcart a 
 		where a.member_id = #{member_id}

select a.*
from tb_goods a 
where a.store_id = #{store_id}
