mapper.select:
select a.only_id, a.youxuandq_id, a.city_file_id, date_format(a.startdate, '%Y-%m-%d ') as startdate, date_format(a.enddate, '%Y-%m-%d ') as enddate, date_format(a.createtime, '%Y-%m-%d ') as createtime, a.createoprator_id, a.dq_title, a.text, date_format(a.opentime,'%H:%i:%s') as opentime, a.isover from tb_youxuandq a where 1=1 and a.youxuandq_id=#{youxuandq_id} and a.city_file_id=#{city_file_id}
and a.isover=#{isover}
limit 1

select a.youxuandq_id, a.city_file_id, date_format(a.startdate, '%Y-%m-%d ') as startdate, date_format(a.enddate, '%Y-%m-%d ') as enddate, date_format(a.createtime, '%Y-%m-%d ') as createtime, a.createoprator_id, a.dq_title, a.text, date_format(a.opentime,'%H:%i:%s') as opentime, a.isover from tb_youxuandq a where 1=1
and a.isover=#{isover}
and a.isover != '99'
and a.city_file_id=#{city_file_id}
order by a.youxuandq_id desc

select b.store_name, b.city_sort_one, c.sort_parent_id, d.sort_parent_id as twosort_parent_id, e.sort_parent_id as threesort_parent_id,
from tb_youxuangoods a left join tb_store_file b on a.store_id=b.store_id left join tb_city_file_sort c on b.city_sort_one=c.city_file_sort_id left join tb_city_file_sort d on b.city_sort_two=d.city_file_sort_id left join tb_city_file_sort e on b.city_sort_three=e.city_file_sort_id where 1=1 and a.goods_status not in ('0','1','2','97','99')
and a.city_file_id=#{pd.city_file_id}
and a.youxuandq_id=#{pd.youxuandq_id}
and (c.sort_parent_id=#{pd.city_file_sort_id} or d.sort_parent_id=#{pd.city_file_sort_id} or e.sort_parent_id=#{pd.city_file_sort_id})
order by cast(a.goods_zkrate as DECIMAL(10,2)) desc order by cast(a.goods_zkrate as DECIMAL(10,2)) order by cast(a.bp_salemoney as DECIMAL(10,2)) desc order by cast(a.bp_salemoney as DECIMAL(10,2)) order by a.createtime desc
