mapper.select:
		select 
				a.zhaoshang_id,
			 	a.company_type,
				a.company_name,
				a.company_contacts,
				a.company_position,
				a.company_phone,
				a.company_mailbox,
				a.province_id,
				a.city_id,
				a.area_id,
  			 	date_format(a.apple_time,	'%Y-%m-%d %H:%i:%s') as apple_time 
		from 
				tb_zhaoshang a 
 		where 
				a.company_type = #{company_type}
				and a.company_name = #{company_name}
				and a.company_contacts = #{company_contacts}
				and a.company_position = #{company_position}
				and a.company_phone = #{company_phone}
				and a.company_mailbox = #{company_mailbox}
				and a.province_id = #{province_id}
				and a.city_id = #{city_id}
				and a.area_id = #{area_id}

select a.zhaoshang_id, a.company_type, a.company_name, a.company_contacts, a.company_position, a.company_phone, a.company_mailbox, a.province_id, a.city_id, a.area_id, date_format(a.apple_time, '%Y-%m-%d %H:%i:%s') as apple_time , b.`name` as province_name, c.`name` as city_name, d.`name` as area_name from tb_zhaoshang a left join tb_pcd b on a.province_id=b.pcd_id left join tb_pcd c on a.city_id=c.pcd_id left join tb_pcd d on a.area_id=d.pcd_id where 1=1
and ( a.company_name like concat(concat("%",#{pd.content}),"%") or a.company_phone like concat(concat("%",#{pd.content}),"%") or a.company_contacts like concat(concat("%",#{pd.content}),"%") or a.company_mailbox like concat(concat("%",#{pd.content}),"%") )
and a.province_id=#{pd.province_id}
and a.city_id=#{pd.city_id}
and a.area_id=#{pd.area_id}
and a.company_type=#{pd.company_type}
order by a.apple_time desc

select a.zhaoshang_id, a.company_type, a.company_name, a.company_contacts, a.company_position, a.company_phone, a.company_mailbox, a.province_id, a.city_id, a.area_id, date_format(a.apple_time, '%Y-%m-%d %H:%i:%s') as apple_time , b.`name` as province_name, c.`name` as city_name, d.`name` as area_name from tb_zhaoshang a left join tb_pcd b on a.province_id=b.pcd_id left join tb_pcd c on a.city_id=c.pcd_id left join tb_pcd d on a.area_id=d.pcd_id where 1=1
and a.province_id=#{ province_id}
and a.city_id=#{ city_id}
and a.area_id=#{ area_id}
and a.company_type=#{ company_type}
