mapper.select:
select a.user_id, a.user_type, ifnull(a.money,'0') as money, a.withdraw_rate, a.arrivalMoney, a.nowuser_money, a.phone, a.withdraw_channel, a.withdraw_number, a.withdraw_username, a.withdraw_type, a.withdraw_status, a.remark, a.exception_remark, a.exception_status, a.chuli_remark, a.dakuan_remark, a.apply_time, a.moneyarrival_time, a.operator_id, a.passmoney_operator_id, a.province_name, a.city_name, a.area_name, a.account_name, a.account_type, a.withdraw_approval_id, a.pass_time, b.operator_name, c.operator_name as dakuan_operator_name from tb_withdraw_approval a left join tb_store_operator b on b.store_operator_id = a.operator_id left join tb_store_operator c on c.store_operator_id = a.passmoney_operator_id where 1=1
and (a.operator_id=#{pd.store_operator_id} or a.passmoney_operator_id=#{pd.store_operator_id})
and a.exception_status=#{pd.exception_status}
and ( a.withdraw_number =#{pd.content} or a.money =#{pd.content}
)
and a.province_name=#{pd.province_name}
and a.city_name=#{pd.city_name}
and a.area_name=#{pd.area_name}
and a.user_type=#{pd.user_type}
and a.account_type=#{pd.account_type}
and date_format(a.apply_time,'%Y-%m-%d')=#{pd.apply_time}
and a.withdraw_type=#{pd.withdraw_type}
and a.withdraw_status=#{pd.withdraw_status}
and date_format(a.pass_time,'%Y-%m-%d') >= #{pd.starttime}
and date_format(a.pass_time,'%Y-%m-%d') <= #{pd.endtime}
and date_format(a.pass_time,'%Y-%m') = #{pd.month}
and a.withdraw_status in ('3','99')
order by a.apply_time desc

		select cast( cast(sum(a.money)  as  DECIMAL(10,2)) as char ) as summoney 
		from 
				tb_withdraw_approval a
		where  
				1=1

					and (a.operator_id=#{pd.store_operator_id} or  a.passmoney_operator_id=#{pd.store_operator_id})


					and a.exception_status=#{pd.exception_status}


					and
						(
						a.withdraw_number =#{pd.content}
						or
						a.money =#{pd.content}

						)


					and a.province_name=#{pd.province_name}


					and a.city_name=#{pd.city_name}


					and a.area_name=#{pd.area_name}


					and a.user_type=#{pd.user_type}


					and a.account_type=#{pd.account_type}


					and date_format(a.apply_time,'%Y-%m-%d')=#{pd.apply_time}


					and a.withdraw_type=#{pd.withdraw_type}


					and a.withdraw_status=#{pd.withdraw_status}


						and date_format(a.pass_time,'%Y-%m-%d') >= #{pd.starttime} 


						and date_format(a.pass_time,'%Y-%m-%d') <= #{pd.endtime} 


						and date_format(a.pass_time,'%Y-%m') = #{pd.month}


					and a.withdraw_status in ('3','99')

  		order by a.apply_time desc

		select cast( cast(sum(a.arrivalMoney)  as  DECIMAL(10,2))  as char ) as summoney 
