					and a.withdraw_type=#{pd.withdraw_type}


					and a.withdraw_status=#{pd.withdraw_status}


						and date_format(a.pass_time,'%Y-%m-%d') >= #{pd.starttime} 


						and date_format(a.pass_time,'%Y-%m-%d') <= #{pd.endtime} 


