mapper.select:
		select 
				 a.money,
				 date_format(a.pass_time,'%Y-%m-%d') as pass_time 
		from 
				tb_withdraw_approval a 
 		where  
				1=1
				and a.withdraw_status='1'

					and
						(
						a.withdraw_number =#{pd.content}
						or
						a.money =#{pd.content}

						)


					and a.province_name=#{pd.province_name}


					and a.city_name=#{pd.city_name}


					and a.area_name=#{pd.area_name}


					and a.user_type=#{pd.user_type}


					and date_format(a.apply_time,'%Y-%m-%d')=#{pd.apply_time}


						and date_format(a.pass_time,'%Y-%m-%d') >= #{pd.starttime} 


						and date_format(a.pass_time,'%Y-%m-%d') <= #{pd.endtime} 


						and date_format(a.pass_time,'%Y-%m') = #{pd.month}

		group by date_format(a.pass_time,'%Y-%m-%d')

		select 
				max(b.money) as money,
				b.user_id,
				b.pass_time,
				b.user_type
		from	
				 (	select 
							 sum(a.money) as money,
							 date_format(a.pass_time,'%Y-%m-%d') pass_time,
							 a.user_id,
							 a.user_type
					from 
							tb_withdraw_approval a 

					where
							1=1
			 				and a.withdraw_status='1'

								and
									(
									a.withdraw_number =#{pd.content}
									or
									a.money =#{pd.content}

									)


								and a.province_name=#{pd.province_name}


								and a.city_name=#{pd.city_name}


								and a.area_name=#{pd.area_name}


								and a.user_type=#{pd.user_type}


								and date_format(a.apply_time,'%Y-%m-%d')=#{pd.apply_time}


									and date_format(a.pass_time,'%Y-%m-%d') >= #{pd.starttime} 


									and date_format(a.pass_time,'%Y-%m-%d') <= #{pd.endtime} 


									and date_format(a.pass_time,'%Y-%m') = #{pd.month}

 					GROUP BY  date_format(a.pass_time,'%Y-%m-%d'),a.user_id  ) b
  		GROUP BY  b.pass_time

select a.user_id, a.user_type, ifnull(a.money,'0') as money, a.nowuser_money, a.phone, a.withdraw_channel, a.withdraw_number, a.withdraw_username, a.withdraw_type, a.withdraw_status, a.remark, a.exception_remark, a.exception_status, a.dakuan_remark, a.apply_time, a.passmoney_operator_id, a.moneyArrival_time, a.operator_id, a.province_name, a.city_name, a.area_name, a.account_name, a.account_type, a.withdraw_approval_id, a.pass_time from tb_withdraw_approval a where withdraw_approval_id in
#{item}
order by a.apply_time desc
