mapper.update:
update tb_oneyuangoods set oneyuangoodsimage_url = #{oneyuangoodsimage_url}, oneyuangoods_name = #{oneyuangoods_name}, oneyuangoodsneed_quantity = #{oneyuangoodsneed_quantity}, isover = #{isover},
oneyuangoodsnowpay_quantity = #{oneyuangoodsnowpay_quantity}, oneyuangoodsrate = oneyuangoodsneed_quantity/#{oneyuangoodsnowpay_quantity},
nowtimes = #{nowtimes}, value_a = #{value_a}, value_a_text = #{value_a_text}, value_b = #{value_b}, value_b_text = #{value_b_text}, member_id = #{member_id}, locky_number = #{locky_number}, overtime = #{overtime}, createoprator_id = #{createoprator_id}, oneyuangoods_id = oneyuangoods_id where oneyuangoods_id = #{oneyuangoods_id}

mapper.select:
select oneyuangoods_id, oneyuangoodsimage_url, oneyuangoods_name, oneyuangoodsneed_quantity, nowtimes, value_a, value_b, value_a_text, value_b_text , date_format(createtime, '%Y-%m-%d %H:%i:%s') as createtime, member_id, locky_number, overtime, isover, createoprator_id, thistype_id from tb_oneyuangoods where 1=1
and isover=#{isover}
order by createtime desc

mapper.update:
update  tb_oneyuanmember
set buy_status=#{buy_status}
where oneyuanmember_id=#{oneyuanmember_id}
