mapper.select:
select contacts_id, contacts_parent_id, contacts_type from tb_contacts where 1=1
and contacts_parent_id=#{contacts_parent_id}
and contacts_id=#{contacts_id}
and contacts_type=#{contacts_type}

select ifnull(a.be_invite_id,'') as be_invite_id, ifnull(a.invite_id,'') as invite_id, ifnull(a.friend_status,'') as friend_status, ifnull(a.be_invite_type,'') as be_invite_type, ifnull(a.invite_type,'') as invite_type, a.friend_id , b.name as be_member_name, b.image_url as be_image_url, b.member_id as be_member_id, c.name as member_name, c.member_id as member_id, c.image_url as image_url, d.store_name as be_store_name, d.store_id as be_store_id, d.pictrue_url as be_pictrue_url, e.store_name as store_name, e.store_id as store_id, e.pictrue_url as pictrue_url from tb_friend a left join tb_member b on a.be_invite_id=b.member_id left join tb_member c on a.invite_id=c.member_id left join tb_store d on a.be_invite_id=d.store_id left join tb_store e on a.invite_id=e.store_id where 1=1
and a.friend_status =#{friend_status}
and a.be_invite_id=#{be_invite_id}
and a.invite_id=#{invite_id}

mapper.update:
			update  tb_friend
			set 
 				friend_status = '2'
			where 
				invite_id = #{invite_id}
 				and be_invite_id = #{be_invite_id}

mapper.select:
select a.member_id, a.image_url, a.name, a.phone, a.vip_level, a.charm_number, a.now_money, a.now_integral, a.isnewmember, a.istuisong, a.islogin, a.createdate, a.logindate, a.wechat_number, a.wxopen_id, a.sex, a.recommended, a.recommended_type, a.status, a.province_name, a.city_name, a.area_name, a.money_pay_number, a.wechat_pay_number, a.alipay_pay_number, a.bank_pay_number, a.apple_pay_number, a.integral_pay_number, a.consumption, a.redpackage_number, a.longitude, a.latitude, a.onetime_red, a.twotime_red, a.cz_number, a.onecontactintegral, a.twocontactintegral from tb_member a where 1=1
and a.phone =#{content}
limit 1

select a.store_id, a.store_name, a.registertel_phone, a.store_vip_id, a.store_biaozhun, a.merchant_level, a.registertime, a.integral_rate, a.service_rate, a.withdraw_rate, a.lowest_score, a.remind_integral, a.sort_score, a.complex_score, a.comment_score, a.recharge_times, a.transaction_times, a.withdraw_times, a.collection_times, a.zan_times, a.bigtype_max, a.smalltype_max, a.goods_max, a.store_file_id, a.check_status, a.merchant_status, a.operate_id, a.longitude, a.latitude, a.pictrue_url, a.tixian_money, a.ispk, a.istuisong from tb_store a where 1=1
and a.store_id =#{content}
limit 1
