mapper.update:
update  tb_order
set 	deletestatus='1'
where   order_id = #{order_id}

mapper.delete:
delete from tb_order
where   order_status = '0'

		and order_id=#{order_id}

mapper.select:
		select	
				cast(count(order_id) as char) number,
				cast(sum(sale_money)  as decimal(15,2))   sumsale_money
  		from 
				tb_order 
		where   	
				date_format(pay_time,'%Y-%m-%d %H:%i:%s') >= #{starttime} 
				and date_format(pay_time,'%Y-%m-%d %H:%i:%s') <= #{endtime}
				and payer_id=#{member_id}	
				and store_id=#{store_id}
				and order_status='1'	
				and tihuo_status='1'

mapper.update:
update tb_guanlian
set status=#{status}
where guanlian_id=#{guanlian_id}

mapper.select:
select a.order_id, a.order_status, a.look_number, date_format(a.createtime,'%Y-%m-%d %H:%i:%s') createtime, a.payer_id, a.payer_id as member_id, a.tihuo_id, a.tihuo_status, date_format(a.startdate,'%Y-%m-%d %H:%i:%s') startdate, date_format(a.enddate,'%Y-%m-%d %H:%i:%s') enddate, ifnull(a.tihuolimit_day,TO_DAYS(a.enddate)-TO_DAYS(a.startdate)) as tihuolimit_day, a.store_id, a.in_jiqi, ifnull(a.pay_sort_type,'1') as pay_sort_type, cast(cast((ifnull(a.sale_money,'0')) as decimal(10,2)) as char) sale_money, cast(cast((ifnull(a.discount_money,'0')) as decimal(10,2)) as char) discount_money, cast(cast((ifnull(a.no_discount_money,'0')) as decimal(10,2)) as char) no_discount_money, cast(cast((ifnull(a.actual_money,'0')) as decimal(10,2)) as char) actual_money, cast(cast((ifnull(a.get_integral,'0')) as decimal(10,2)) as char) get_integral, cast(cast((ifnull(a.user_balance,'0')) as decimal(10,2)) as char) user_balance, cast(cast((ifnull(a.user_integral,'0')) as decimal(10,2)) as char) user_integral, cast(cast((ifnull(a.discount_after_money,'0')) as decimal(10,2)) as char) discount_after_money, cast(cast((ifnull(a.store_renmai_money,'0')) as decimal(10,2)) as char) store_renmai_money, ifnull(a.channel,'nowpay') as channel, ifnull(a.channel,'nowpay') as pay_way, ifnull(a.discount_content,'') as discount_content, c.phone as pay_phone, c.member_id from tb_order a left join tb_store b on a.store_id=b.store_id left join tb_member c on a.payer_id=c.member_id where 1=1
and a.order_id=#{order_id}
and a.tihuo_id=#{tihuo_id}

		select	
				cast(count(order_id) as char) number,
  		from 
				tb_order 
		where   	
				date_format(pay_time,'%Y-%m-%d %H:%i:%s') >= #{starttime} 
				and date_format(pay_time,'%Y-%m-%d %H:%i:%s') <= #{endtime}
				and payer_id=#{member_id}	
				and store_id=#{store_id}
				and order_status='1'	
				and tihuo_status='1'
