mapper.update:
update  
		tb_store
	set 
		merchant_status=#{merchant_status}
	where 
		store_id = #{store_id}

mapper.select:
select zan_id, be_zan_id from tb_zan where 1=1
and zan_id=#{member_id}
and be_zan_id=#{store_id}

mapper.insert:
		insert into tb_zan(
 			zan_id,	
			be_zan_id
		) values (
 			#{zan_id},	
			#{be_zan_id}
		)

mapper.select:
select a.store_name, ifnull(a.pictrue_url,"") pictrue_url, a.integral_rate, a.complex_score, a.comment_score, ifnull(cast(f.ordernumber as char) , '0') as transaction_times, a.zan_times, a.sort_score, a.check_status, a.longitude, a.latitude , a.store_id, cast((cast(round( 6378.138 * 2 * asin( sqrt( pow( sin(( abs(a.latitude * pi() / 180 - #{latitude} * pi() / 180) ) / 2 ), 2 ) + cos(a.latitude * pi() / 180) * cos( #{latitude} * pi() / 180) * pow( sin(( abs(a.longitude * pi() / 180 - #{longitude} * pi() / 180) ) / 2 ), 2 ))) ,3) as decimal(10,1))) as char) as distance, a.merchant_level from tb_store a left join tb_store_file b on a.store_id=b.store_id left join tb_city_file_sort c on b.city_sort_one=c.city_file_sort_id left join tb_city_file_sort d on b.city_sort_two=d.city_file_sort_id left join tb_city_file_sort e on b.city_sort_three=e.city_file_sort_id left join (select count(*) as ordernumber , store_id from tb_store_wealthhistory where profit_type='3' and process_status='1' GROUP BY store_id ) as f on a.store_id=f.store_id where a.check_status='1' and a.merchant_status != '4' and b.open_status='1' and b.biaozhun_status='1'
and ( a.store_name like CONCAT(CONCAT("%",#{content}),"%") or b.address like CONCAT(CONCAT("%",#{content}),"%") )
and (c.sort_parent_id=#{city_file_sort_id} or d.sort_parent_id=#{city_file_sort_id} or e.sort_parent_id=#{city_file_sort_id})
and (b.city_sort_one=#{city_file_sort_id} or b.city_sort_two=#{city_file_sort_id} or b.city_sort_three=#{city_file_sort_id})
and b.province_name=#{province_name}
and b.city_name=#{city_name}
and b.area_name=#{area_name}
and a.store_id in( select m.store_id from tb_store_marketing m where m.open_status='1' and m.isguoqi='0'
and m.marketing_type='2' and m.marketing_type='7' and m.marketing_type='1'
)
order by round( 6378.138 * 2 * asin( sqrt( pow( sin(( abs(a.latitude * pi() / 180 - #{latitude} * pi() / 180) ) / 2 ), 2 ) + cos(a.latitude * pi() / 180) * cos( #{latitude} * pi() / 180) * pow( sin(( abs(a.longitude * pi() / 180 - #{longitude} * pi() / 180) ) / 2 ), 2 ))) ,3), a.registertime order by cast(a.comment_score as decimal(10,1) ) desc, a.registertime order by cast(a.integral_rate as decimal(10,2) ) desc, a.registertime order by cast(f.ordernumber as signed) desc, a.registertime order by cast(a.complex_score as decimal(10,1) ) desc , a.registertime
limit #{startnumber},10

select *
from tb_store_way
where store_id=#{store_id}

mapper.delete:
delete from tb_collect
where 

		collect_id=#{collect_id}
		member_id=#{member_id} and store_id=#{store_id}

mapper.select:
select count(*)
from tb_store_wealthhistory
where profit_type='3' and store_id=#{store_id}

select cast( sum(number) as char )
from tb_store_wealthhistory
where profit_type='3' and store_id=#{store_id}

mapper.insert:
