 		) values (
   			#{store_renmaijf_id},	
   			#{order_id} 
		)

mapper.select:
select a.only_store_id, a.store_id, a.store_name, ifnull(a.pictrue_url,"") pictrue_url, a.integral_rate, a.complex_score, a.comment_score, ifnull(cast(f.ordernumber as char) , '0') as transaction_times, a.zan_times, a.sort_score, a.check_status, a.longitude, a.latitude , cast((cast(round( 6378.138 * 2 * asin( sqrt( pow( sin(( abs(a.latitude * pi() / 180 - ifnull(#{pd.latitude},'30') * pi() / 180) ) / 2 ), 2 ) + cos(a.latitude * pi() / 180) * cos( ifnull(#{pd.latitude},'30') * pi() / 180) * pow( sin(( abs(a.longitude * pi() / 180 - ifnull(#{pd.longitude},'120') * pi() / 180) ) / 2 ), 2 ))) ,3) as decimal(10,1))) as char) as distance, a.merchant_level from tb_store a left join tb_store_file b on a.store_id=b.store_id left join tb_city_file_sort c on b.city_sort_one=c.city_file_sort_id left join tb_city_file_sort d on b.city_sort_two=d.city_file_sort_id left join tb_city_file_sort e on b.city_sort_three=e.city_file_sort_id left join (select count(*) as ordernumber , store_id from tb_store_wealthhistory where profit_type='3' and process_status='1' GROUP BY store_id ) as f on a.store_id=f.store_id where a.check_status='1' and a.merchant_status != '4' and b.open_status='1' and b.biaozhun_status='1'
and ( a.store_name like CONCAT(CONCAT("%",#{pd.content}),"%") or b.address like CONCAT(CONCAT("%",#{pd.content}),"%") )
and (c.sort_parent_id=#{pd.city_file_sort_id} or d.sort_parent_id=#{pd.city_file_sort_id} or e.sort_parent_id=#{pd.city_file_sort_id}) and (b.city_sort_one=#{pd.city_file_sort_id} or b.city_sort_two=#{pd.city_file_sort_id} or b.city_sort_three=#{pd.city_file_sort_id})
and b.province_name=#{pd.province_name}
and b.city_name=#{pd.city_name}
and b.area_name=#{pd.area_name}
order by round( 6378.138 * 2 * asin( sqrt( pow( sin(( abs(a.latitude * pi() / 180 - #{pd.latitude} * pi() / 180) ) / 2 ), 2 ) + cos(a.latitude * pi() / 180) * cos( #{pd.latitude} * pi() / 180) * pow( sin(( abs(a.longitude * pi() / 180 - #{pd.longitude} * pi() / 180) ) / 2 ), 2 ))) ,3),cast(a.complex_score as decimal(10,1) ) desc , a.registertime order by cast(a.comment_score as decimal(10,1) ) desc, cast(a.complex_score as decimal(10,1) ) desc ,a.registertime order by cast(a.integral_rate as decimal(10,2) ) desc, cast(a.complex_score as decimal(10,1) ) desc , a.registertime order by cast(f.ordernumber as signed) desc, cast(a.complex_score as decimal(10,1) ) desc , a.registertime order by cast(a.complex_score as decimal(10,1) ) desc , a.registertime
