mapper.select:
select count(*) from tb_store_redpackets a where a.redpackage_status = '0' and a.isguoqi='0' and cast(a.redpackage_number as signed) > cast(a.overget_number as signed) and to_days(now()) >= to_days(a.starttime) and to_days(now()) <= to_days(a.endtime) and redpackage_type in ('1','2') and a.store_id=#{store_id}
and a.store_redpackets_id not in( select b.store_redpackets_id from tb_member_redpackets b where b.member_id=#{member_id} )
group by a.store_id

select distinct a.store_name, a.store_id, a.pictrue_url, a.latitude, a.longitude from tb_store a
join tb_order b on a.store_id = b.store_id and b.payer_id=#{pd.member_id}
join tb_store_file c on a.store_id=c.store_id and c.city_name=#{pd.city_name}
where 1=1
and a.store_name like concat(concat("%",#{pd.store_name}),"%") order by a.registertime desc
order by b.createtime desc
