package com.tianer.util;

import java.io.FileInputStream;
import java.io.FileOutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.OutputStream;

import sun.misc.BASE64Decoder;
import sun.misc.BASE64Encoder;

public class ImageBase64Test 
{
    public static void main(String[] args)
    {
        String strImg = GetImageStr("c://1.jpg");
        System.out.println(strImg);
           String str="";
             GenerateImage(strImg,"E://1.png");
    }
    //base64
    public static String GetImageStr(String imgurl)
    {
    	//Base64
//        String imgFile = "d://test.jpg";//
        InputStream in = null;
        byte[] data = null;
        //
        try 
        {
            in = new FileInputStream(imgurl);        
            data = new byte[in.available()];
            in.read(data);
            in.close();
        } 
        catch (IOException e) 
        {
            e.printStackTrace();
        }
        //Base64
        BASE64Encoder encoder = new BASE64Encoder();
        return encoder.encode(data);//Base64
    }
    
    
    //base64
    public static boolean GenerateImage(String imgStr,String imgFilePath)
    {   //Base64
        if (imgStr == null) //
            return false;
        BASE64Decoder decoder = new BASE64Decoder();
        try 
        {
            //Base64
            byte[] b = decoder.decodeBuffer(imgStr);
            for(int i=0;i<b.length;++i)
            {
                if(b[i]<0)
                {//
                    b[i]+=256;
                }
            }
            //jpeg
//            String imgFilePath = "c://"+imgname+".png";//
            OutputStream out = new FileOutputStream(imgFilePath);    
            out.write(b);
            out.flush();
            out.close();
            return true;
        } 
        catch (Exception e) 
        {
            return false;
        }
    }
}

